var nodeFlows = require('../nodeFlows');

exports.executeActivity = executeActivity;

function executeActivity(workflow, args, callback) {

    args.data.workflowid = workflow._id.toString();

    nodeFlows.getCollection(args.collectionName, function(collection) {
        collection.insert(args.data, function(err, docs) {
            callback({ success: true, activity: "insertIntoCollectionActivity" });
        });
    });
}
