//Removes this workflow from storage

var nodeFlows = require('../nodeFlows');
var statManager = require('../statManager.js');

exports.executeActivity = executeActivity;

function executeActivity(workflow, args, callback) {

    statManager.statsEmitter.emit('deleteWorkflowInstance', {workflowId: workflow._id.toString()});

    callback({success: true, activity: "removeMe"});
}
