//Global settings

//Develop
//Stress
//TestMongo
var environment = "Develop";


var globalSettingsDevelop = {
    logToConsole:true, 
    logToMemory:true, 
    historyEnabled: true,
    workflowHistoryEnabled: true,
    queueWorkerHistoryEnabled: true,
    queueItemHistoryEnabled:true,
    persistenceProvider:"nodeFlowPersistence_MOQ",
    writeHistoryToLog: false
    };

var globalSettingsStress = {
    logToConsole:false, 
    logToMemory:false, 
    historyEnabled: false,
    workflowHistoryEnabled: false,
    queueWorkerHistoryEnabled: false,
    queueItemHistoryEnabled:false,
    persistenceProvider:"nodeFlowPersistence_MOQ",
    writeHistoryToLog:false
    };

    var globalSettingsMongo = globalSettingsDevelop;
    globalSettingsMongo.persistenceProvider = "nodeFlowPersistence_Mongo";

switch (environment){
    case "Develop":
        exports.globalSettings = globalSettingsDevelop;
        break;
    case "Stress":
        exports.globalSettings = globalSettingsStress;
        break;
    case "TestMongo":
        exports.globalSettings = globalSettingsMongo;
        break;
    default:
        exports.globalSettings = globalSettingsDevelop;
        break;
}



