//Workflow to simulate the cooks activities

var workflowDefinition = {
    Name: "PizzaDelivery",
    workers: 7,
    Steps: [
        { Name: "StartWorkflow",
            Type: "Init",
            args:
                { status: "Received", currentStepName: "DeliveryRequestReceived" }
        },
        { Name: "DeliveryRequestReceived",
            Type: "writeLogActivity",
            args:
                { text: "Delivery Request received" }
        },
        { Name: "LoadPizzaInCar",
            Type: "writeLogActivity",
            args:
                { text: "Loading Pizza in car" }
        },
        { Name: "DriveToCustomer",
            Type: "waitForTimer",
            args:
                { waitTime: 1000 }
        },
        { Name: "PizzaDelivered",
            Type: "updateInitiatingWorkflowActivity",
            args:
                { 'status': 'Delivered'}
        },
        { Name: "TrackPizzasDelivered",
            Type: "trackStatistic",
            args:
                { 'statistic': 'PizzasDelivered'}
        },
        { Name: "Complete",
            Type: "completeWorkflow",
            args:
                {}
        }

    ]
}


exports.workflowDefinition = workflowDefinition;
