module.exports = function (app) {

    var nodeFlows = require('nodeFlows');

    var workflowManager = nodeFlows.workflowManager;

    app.get('/PizzaSite/PizzaDashboard', function (req, res) {
        workflowManager.getActiveWorkflows(function (activeWorkflows) {
            res.render('../node_modules/nodeFlowsExpressClient/views/PizzaSite/pizzaDashboard', { title: 'Active Workflows', workflows: activeWorkflows })
        });
    });



}